mapper.select:
SELECT PAGINGT.* FROM (
SELECT 
F.FORMID,F.FORMTYPE,F.FORMNAME,F.FORMDESC,F.CREATOR,F.CREATORNAME,F.CREATETIME,V.UPDATETIME,V.CHECKOUTBY,V.CHECKOUTBYNAME
from TS_FORM F,TS_FORM_VERSION V WHERE F.FORMID=V.FORMID AND V.VERSIONID='0' AND F.STATUS!='02' 

    AND FORMID = #{form.formid,jdbcType=VARCHAR}


    AND FORMTYPE = #{form.formtype,jdbcType=VARCHAR}


    AND FORMNAME like CONCAT('%',UPPER(#{form.formname,jdbcType=VARCHAR}),'%')


    AND FORMDESC like CONCAT('%',UPPER(#{form.formdesc,jdbcType=VARCHAR}),'%')


    AND CREATOR like CONCAT('%',UPPER(#{form.creator,jdbcType=VARCHAR}),'%')


    AND CREATORNAME like CONCAT('%',UPPER(#{form.creatorname,jdbcType=VARCHAR}),'%')


    AND CREATETIME = #{form.createtime,jdbcType=TIMESTAMP}


    AND STATUS = #{form.status,jdbcType=VARCHAR}

) PAGINGT 

    ${orderby} 

limit #{fromRow,jdbcType=NUMERIC}, #{rows,jdbcType=NUMERIC}

SELECT COUNT(*) AS COUNT 
from TS_FORM F,TS_FORM_VERSION V WHERE F.FORMID=V.FORMID AND V.VERSIONID='0' AND F.STATUS!='02' 

    AND FORMID = #{form.formid,jdbcType=VARCHAR}


    AND FORMTYPE = #{form.formtype,jdbcType=VARCHAR}


    AND UPPER(FORMNAME) like CONCAT('%',UPPER(#{form.formname,jdbcType=VARCHAR}),'%')


    AND UPPER(FORMDESC) like CONCAT('%',UPPER(#{form.formdesc,jdbcType=VARCHAR}),'%')


    AND UPPER(CREATOR) like CONCAT('%',UPPER(#{form.creator,jdbcType=VARCHAR}),'%')


    AND UPPER(CREATORNAME) like CONCAT('%',UPPER(#{form.creatorname,jdbcType=VARCHAR}),'%')


    AND CREATETIME = #{form.createtime,jdbcType=TIMESTAMP}


    AND STATUS = #{form.status,jdbcType=VARCHAR}
