mapper.select:

select 

,

from ts_form_version
where FORMID = #{formid,jdbcType=VARCHAR}
  and VERSIONID = #{versionid,jdbcType=VARCHAR}

mapper.delete:

delete from ts_form_version
where FORMID = #{formid,jdbcType=VARCHAR}
  and VERSIONID = #{versionid,jdbcType=VARCHAR}

mapper.select:
select ifnull(MAX(VERSIONID), '1') AS MAX
from ts_form_version
where FORMID = #{formid,jdbcType=VARCHAR}

SELECT PAGINGT.* FROM ( SELECT V.FORMID, V.VERSIONID, V.VERSIONNAME, V.VERSIONDESC, V.CREATOR, V.CREATORNAME, V.CREATETIME, V.UPDATETIME, V.ISDEFAULT, V.STATUS, V.CHECKOUTBY, V.CHECKOUTBYNAME, H.UPDATETIME AS HUPDATETIME, H.ISAUTOSAVE, H.CHANGENO, H.CHANGEUSERID, H.CHANGEUSERNAME from TS_FORM_VERSION V LEFT JOIN (SELECT FORMID,VERSIONID,UPDATETIME,ISAUTOSAVE,CHANGENO,CHANGEUSERID,CHANGEUSERNAME FROM TS_FORM_HISTORY WHERE FORMID=#{args.formid,jdbcType=VARCHAR} AND VERSIONID = #{args.versionid,jdbcType=VARCHAR} ORDER BY UPDATETIME DESC LIMIT 0,1) H ON V.FORMID=H.FORMID AND V.VERSIONID=H.VERSIONID ) PAGINGT where STATUS!='02'
AND FORMID = #{args.formid,jdbcType=VARCHAR}
AND VERSIONID = #{args.versionid,jdbcType=VARCHAR}
AND UPPER(VERSIONNAME) like CONCAT('%',UPPER(#{args.versionname,jdbcType=VARCHAR}),'%')
AND UPPER(VERSIONDESC) like CONCAT('%',UPPER(#{args.versiondesc,jdbcType=VARCHAR}),'%')
AND UPDATETIME = #{args.updatetime,jdbcType=VARCHAR}
AND UPPER(CREATOR) like CONCAT('%',UPPER(#{args.creator,jdbcType=VARCHAR}),'%')
AND UPPER(CREATORNAME) like CONCAT('%',UPPER(#{args.creatorname,jdbcType=VARCHAR}),'%')
AND CREATETIME = #{args.createtime,jdbcType=TIMESTAMP}
AND STATUS = #{args.status,jdbcType=TIMESTAMP}
AND UPPER(CHECKOUTBY) like CONCAT('%',UPPER(#{args.checkoutby,jdbcType=TIMESTAMP}),'%')
AND UPPER(CHECKOUTBYNAME) like CONCAT('%',UPPER(#{args.checkoutbyname,jdbcType=TIMESTAMP}),'%')
AND UPPER(H.CHANGEUSERID) like CONCAT('%',UPPER(#{args.changeuserid,jdbcType=VARCHAR}),'%')
AND UPPER(H.CHANGEUSERNAME) like CONCAT('%',UPPER(#{args.changeusername,jdbcType=VARCHAR}),'%')
${orderby}
limit #{fromRow,jdbcType=NUMERIC}, #{rows,jdbcType=NUMERIC}

SELECT COUNT(*) AS COUNT FROM (SELECT V.FORMID, V.VERSIONID, V.VERSIONNAME, V.VERSIONDESC, V.CREATETIME, V.UPDATETIME, V.ISDEFAULT, V.STATUS, V.CHECKOUTBY, V.CHECKOUTBYNAME, H.UPDATETIME AS HUPDATETIME, H.ISAUTOSAVE, H.CHANGENO, H.CHANGEUSERID, H.CHANGEUSERNAME from TS_FORM_VERSION V LEFT JOIN (SELECT FORMID,VERSIONID,UPDATETIME,ISAUTOSAVE,CHANGENO,CHANGEUSERID,CHANGEUSERNAME FROM TS_FORM_HISTORY WHERE FORMID=#{args.formid,jdbcType=VARCHAR} AND VERSIONID = #{args.versionid,jdbcType=VARCHAR} ORDER BY UPDATETIME DESC LIMIT 0,1) H ON V.FORMID=H.FORMID AND V.VERSIONID=H.VERSIONID) PAGINGT where STATUS!='02'
AND FORMID = #{args.formid,jdbcType=VARCHAR}
AND VERSIONID = #{args.versionid,jdbcType=VARCHAR}
AND UPPER(VERSIONNAME) like CONCAT('%',UPPER(#{args.versionname,jdbcType=VARCHAR}),'%')
AND UPPER(VERSIONDESC) like CONCAT('%',UPPER(#{args.versiondesc,jdbcType=VARCHAR}),'%')
AND UPDATETIME = #{args.updatetime,jdbcType=VARCHAR}
AND UPPER(CREATOR) like CONCAT('%',UPPER(#{args.creator,jdbcType=VARCHAR}),'%')
AND UPPER(CREATORNAME) like CONCAT('%',UPPER(#{args.creatorname,jdbcType=VARCHAR}),'%')
AND CREATETIME = #{args.createtime,jdbcType=TIMESTAMP}
AND STATUS = #{args.status,jdbcType=TIMESTAMP}
AND UPPER(CHECKOUTBY) like CONCAT('%',UPPER(#{args.checkoutby,jdbcType=TIMESTAMP}),'%')
AND UPPER(CHECKOUTBYNAME) like CONCAT('%',UPPER(#{args.checkoutbyname,jdbcType=TIMESTAMP}),'%')
AND UPPER(H.CHANGEUSERID) like CONCAT('%',UPPER(#{args.changeuserid,jdbcType=VARCHAR}),'%')
AND UPPER(H.CHANGEUSERNAME) like CONCAT('%',UPPER(#{args.changeusername,jdbcType=VARCHAR}),'%')
