mapper.select:

select 

,

from ts_form_history
where FORMID = #{formid,jdbcType=VARCHAR}
  and CHANGENO = #{changeno,jdbcType=BIGINT}

mapper.delete:

delete from ts_form_history
where FORMID = #{formid,jdbcType=VARCHAR}
  and CHANGENO = #{changeno,jdbcType=BIGINT}

mapper.insert:

insert into ts_form_history (FORMID, CHANGENO, UPDATETIME, 
  ISAUTOSAVE, VERSIONID, CHANGEUSERID, 
  CHANGEUSERNAME, FORMDATA, CHANGING
  )
values (#{formid,jdbcType=VARCHAR}, #{changeno,jdbcType=BIGINT}, #{updatetime,jdbcType=TIMESTAMP}, 
  #{isautosave,jdbcType=CHAR}, #{versionid,jdbcType=VARCHAR}, #{changeuserid,jdbcType=VARCHAR}, 
  #{changeusername,jdbcType=VARCHAR}, #{formdata,jdbcType=LONGVARCHAR}, #{changing,jdbcType=LONGVARCHAR}
  )

mapper.update:

update ts_form_history
set UPDATETIME = #{updatetime,jdbcType=TIMESTAMP},
  ISAUTOSAVE = #{isautosave,jdbcType=CHAR},
  VERSIONID = #{versionid,jdbcType=VARCHAR},
  CHANGEUSERID = #{changeuserid,jdbcType=VARCHAR},
  CHANGEUSERNAME = #{changeusername,jdbcType=VARCHAR},
  FORMDATA = #{formdata,jdbcType=LONGVARCHAR},
  CHANGING = #{changing,jdbcType=LONGVARCHAR}
where FORMID = #{formid,jdbcType=VARCHAR}
  and CHANGENO = #{changeno,jdbcType=BIGINT}


update ts_form_history
set UPDATETIME = #{updatetime,jdbcType=TIMESTAMP},
  ISAUTOSAVE = #{isautosave,jdbcType=CHAR},
  VERSIONID = #{versionid,jdbcType=VARCHAR},
  CHANGEUSERID = #{changeuserid,jdbcType=VARCHAR},
  CHANGEUSERNAME = #{changeusername,jdbcType=VARCHAR}
where FORMID = #{formid,jdbcType=VARCHAR}
  and CHANGENO = #{changeno,jdbcType=BIGINT}

mapper.select:
select ifnull(MAX(CHANGENO), 1) AS MAX
from ts_form_history
where FORMID = #{formid,jdbcType=VARCHAR} and VERSIONID = #{versionid,jdbcType=VARCHAR}
